mapper.select:
select * from board join member on(ref_member_username = member_username) where board_type='Don' and board_status='Y'
and ref_category_id=#{cate}
order by board_id desc

select *
from cheer
where ref_board_id=#{boardId} and ref_member_username=#{memberUserName}

mapper.update:
update board
set board_count = board_count+1
where board_id=#{bId}
