mapper.update:
update member
set MEMBER_ROSE = MEMBER_ROSE + #{roseNum}
where MEMBER_USERNAME = #{id}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_USERNAME = #{userName}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_NICKNAME = #{nickName}

mapper.update:
UPDATE MEMBER
SET MEMBER_STATUS = 'N'
WHERE MEMBER_USERNAME = #{userName}

mapper.select:
select MESSAGE_ID,REF_BOARD_ID,MESSAGE_CREATE_DATE,MESSAGE_TITLE,MESSAGE_CONTENT,MESSAGE_CHECK,MESSAGE_STATUS, SENDER_USERNAME,RECEIVER_USERNAME,BOARD_TYPE from message join board on(REF_BOARD_ID = BOARD_ID) where MESSAGE_STATUS = 'Y'
and RECEIVER_USERNAME = #{id}
and SENDER_USERNAME = #{id}
order by MESSAGE_CREATE_DATE desc

select count(*)
from message
where MESSAGE_STATUS = 'Y'

	 and RECEIVER_USERNAME = #{id}


	and SENDER_USERNAME = #{id}

mapper.update:
