mapper.update:
update member
set MEMBER_ROSE = MEMBER_ROSE + #{roseNum}
where MEMBER_USERNAME = #{id}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_USERNAME = #{userName}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_NICKNAME = #{nickName}

mapper.update:
UPDATE MEMBER
SET MEMBER_STATUS = 'N'
WHERE MEMBER_USERNAME = #{userName}
